cout << "DIVISION BY ZERO." <<endl;
return;
}
rep(j, m) mat[i][j] = mat[i][j]/pivot;
rep(j, n) {
if(i == j) continue;
comm = mat[j][i];
rep2(k, i, m) {
mat[j][k] = mat[j][k] - (comm*mat[i][k]);
}
}
}

